    types: ["water"],
    evolution: "squirtle",
    url: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/8.png"
  },
  {
    id: 9,
    name: "blastoise",
    types: ["water"],
    evolution: "wartortle",
    url: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/9.png"
  },
  {
    id: 10,
    name: "caterpie",
    types: ["bug"],
    evolution: null,
    url: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/10.png"
  }
];

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { pokemon }.pokemon;
    // this.handleChange = this.handleChange.bind(this);
  }
  //version corta
  // handleChange(event) {
  //   let valueInput = event.target.value;

  //   this.setState(prevState => ({
  //     value: prevState.value === "" ? `${valueInput}` : `${valueInput}`
  //   }));
  //   console.log(this.state.value);
  // }

  render() {
    pokemon.map((item, index) => {
      return <li>{item.name}</li>;
    });
    return (
      <>
        <h1>{this.state.value}</h1>
        <ul>{this.state}</ul>
        <h2>Hola Mundo</h2>
      </>
    );
  }
}

export default App;
